passport.use(new LocalStrategy({
  usernameField: 'id',
  //form에서 id라는 이름을 가진 것
  passwordField: 'pw',
  //form에서 pw라는 이름을 가진 것.
  session: true,
  //로그인 후 세션을 저장할 것인지
  passReqToCallback: false //아이디, 비번 말고도 다른 정보 검증시 true로 바꾸고, funciton안에 파라미터 넣어주면 됨 

}, function (입력한아이디, 입력한비번, done) {
  //console.log(입력한아이디, 입력한비번);
  db.collection('login').findOne({
    id: 입력한아이디
  }, function (에러, 결과) {
    if (에러) return done(에러);
    if (!결과) return done(null, false, {
      message: '존재하지않는 아이디요'
    });

    if (입력한비번 == 결과.pw) {
      //DB에 아이디가 있으면, 입력한 비번과 결과.pw 비교
      return done(null, 결과); //done(서버에러,성공시 사용자 DB 데이터,에러메세지);
    } else {
      return done(null, false, {
        message: '비번틀렸어요'
      });
    }
  });
})); //세션 만들기 - 로그인 성공시 발동

