}); //이러한 세션 데이터를 가진 사람을 DB에서 찾아주세요

passport.deserializeUser(function (id, done) {
  //db에서 위에 있는 user.id 로 유저를 찾은 뒤에, 유저 정 done(null,{여기에 넣음});
  db.collection('login').findOne({
